quickStart.pythonExample:
# pip install requests
import requests

headers = {
    'Authorization': 'ApiKey YOUR_API_KEY'
}

response = requests.get(
    'https://oss.xiaojinpro.com/files',
    headers=headers
)
print(response.json())

quickStart.jsExample:
// npm install axios
import axios from 'axios';

const client = axios.create({
  baseURL: 'https://oss.xiaojinpro.com',
  headers: {
    'Authorization': 'ApiKey YOUR_API_KEY'
  }
});

const files = await client.get('/files');
console.log(files.data);
